mapper.select:
select u.u_id, u_number, u_password, u_name, u_email, u_tel, p_id, p_intro, p_head, p_birthday, p_sex
from user_info u left join personage_info p on u.u_id = p.u_id
where u_number = #{userNumber} and u_password = #{userPassword}

mapper.update:
      update user_info as u
       left join personage_info as p
	    on u.u_id = p.u_id
	     set u.u_name = #{userName}, p.p_intro = #{personage.intro},
	      p.p_head = #{personage.head}, p.p_birthday =  #{personage.birthday}, p.p_sex =  #{personage.sex}
	      where u.u_id = #{userId}
